package week2.day1;

public class Jumpbreakstatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 0; i <=5; i++) {
			
			if(i==3) {
				break; //terminate the particular process
			}
			
			System.out.println(i);
			
		}

	}

}
