public class CheckPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=5;
		
		if (a>0) {
			
			System.out.println(a +" "+ "is positive number");
		}
		else {
			System.out.println(a +" "+ "is Negative number");
		}

	}

}
